import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../App.css';

function HomePage() {
  const navigate = useNavigate();
  const [user, setUser] = React.useState(null);
  const [connected, setConnected] = React.useState(true);
  
  React.useEffect(() => {
    // Âä†ËΩΩÁî®Êà∑‰ø°ÊÅØ
    const userStr = localStorage.getItem('user');
    if (userStr) {
      try {
        const userData = JSON.parse(userStr);
        setUser(userData);
      } catch (e) {
        console.error('Failed to parse user data', e);
        setUser(null);
      }
    }
  }, []);
  
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };
  
  const handleChatClick = () => {
    navigate('/chat');
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <div className="logo">
          <span className="logo-text">FunTool</span>
          <span className="logo-ai">AI</span>
          <span className="logo-heart">‚ù§Ô∏è</span>
        </div>
        <div className="user-controls">
          {user ? (
            <>
              <span className="username">{user.username}</span>
              <Link to="/history" className="history-link">ÂéÜÂè≤ËÆ∞ÂΩï</Link>
              <button className="logout-button" onClick={handleLogout}>ÈÄÄÂá∫</button>
            </>
          ) : (
            <div className="auth-buttons">
              <Link to="/login" className="login-button">ÁôªÂΩï</Link>
              <Link to="/register" className="signup-button">Ê≥®ÂÜå</Link>
            </div>
          )}
          <div className="connection-status">
            {connected ? <span className="status-connected">Âú®Á∫ø</span> : <span className="status-disconnected">Á¶ªÁ∫ø</span>}
          </div>
        </div>
      </header>

      <div className="main-content">
        <div className="hero-section">
          <h1 className="main-title">
            RIZZ AI Assistant<br />
            Free RIZZ GPT and Fun<br />
            <span>AI Dating Assistant</span>
          </h1>
          <p className="subtitle">RIZZ GPT makes every encounter both easy and meaningful.</p>
        </div>
        
        <div className="buttons-container">
          <div className="button-card reply-btn">
            <span className="button-icon">‚ù§Ô∏è</span>
            <span className="button-text">Reply with Screenshot</span>
          </div>
          <div className="button-card reply-btn reply-manual" onClick={handleChatClick}>
            <span className="button-icon">‚úèÔ∏è</span>
            <span className="button-text">Reply with Manual Input</span>
          </div>
          <div className="button-card">
            <span className="button-icon">üòò</span>
            <span className="button-text">Pick up lines</span>
          </div>
          <div className="button-card">
            <span className="button-icon">ü•∞</span>
            <span className="button-text">Rizz lines</span>
          </div>
          <div className="button-card">
            <span className="button-icon">üìã</span>
            <span className="button-text">Tinder bios</span>
          </div>
          <div className="button-card">
            <span className="button-icon">üñºÔ∏è</span>
            <span className="button-text">RIZZ Face</span>
          </div>
        </div>

        <div className="features-section">
          <h2 className="section-title">Why Choose RIZZ AI?</h2>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">üí¨</div>
              <h3>Smart Conversations</h3>
              <p>Our AI understands context and helps create engaging conversations</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">üîí</div>
              <h3>Privacy First</h3>
              <p>Your conversations are private and secure</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">‚ö°</div>
              <h3>Fast Responses</h3>
              <p>Get instant suggestions for your dating conversations</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
